/* Function to configure Port Pins based on user configuration & Higher App 
 * configurations.
 */
void IO002_Init(void)
{
   /* <<<DD_IO002_API_1>>> */   
  /* Configuration of 2 Port 1 based on User configuration */
  PORT2->PHCR0 &= ~(PORT2_PHCR0_PH1_Msk);
  IO002_Handle0.PortRegs->IOCR0 |= (0U << 11);
}

